"use strict";window.onload=function(){var e,t=new RegExp(/^1(3|4|5|7|8)\d{9}$/);document.querySelector("#check-phone").addEventListener("click",function(){e=document.querySelector("input[name=phone]").value,t.test(e)?alert("手机号码输入正确"):alert("手机号码输入错误")});var n,c=new RegExp(/(\b\w+)\s+\1\b/g);document.querySelector("#check-string").addEventListener("click",function(){n=document.querySelector("textarea[name=string]").value,c.test(n)?alert("存在相邻的重复单词"):alert("不存在相邻的重复单词")})};